import React, { useState, useEffect } from 'react';

const PomodoroTimer = () => {
  // State variables for minutes, seconds, and timer status
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(10);
  const [isRunning, setIsRunning] = useState(true);

  // Function to play the notification audio
  const playAudio = (event) => {
    // Create an Audio object with the correct file path
    const audio = new Audio('./notification.mp3');
    console.log('Audio Object:', audio);

    // Check if audio is loaded before attempting to play
    if (audio) {
      audio.play();
    } else {
      console.error('Audio not loaded.');
    }

    // Prevent default behavior to satisfy user-generated event requirement
    event.preventDefault();
  };

  // Effect hook to manage the timer logic
  useEffect(() => {
    let timer;

    // Function to update the timer
    const updateTimer = () => {
      if (seconds > 0) {
        setSeconds(seconds - 1);
      } else {
        if (minutes > 0) {
          setMinutes(minutes - 1);
          setSeconds(59);
        } else {
          clearInterval(timer);
          setIsRunning(false);
          handleTimerCompletion();
        }
      }
    };

    // Set up the timer when the component mounts or when isRunning changes
    if (isRunning) {
      timer = setInterval(updateTimer, 1000);
    }

    // Clean up the timer on component unmount or when isRunning changes
    return () => clearInterval(timer);
  }, [minutes, seconds, isRunning]);

  // Function to handle timer completion
  const handleTimerCompletion = (event) => {
    // If the timer completes, toggle between 25 and 5 minutes
    if (minutes === 0 && seconds === 0) {
      if (isRunning) {
        playAudio(event);
        setMinutes(5);
      } else {
        setMinutes(25);
      }
      setIsRunning(true);
    }
  };

  // Function to handle the "Pause/Resume" button click
  const handlePauseResumeClick = (event) => {
    setIsRunning(!isRunning);
    handleTimerCompletion(event);
  };

  // Render the Pomodoro Timer component
  return (
    <div>
      <p>
        {String(minutes).padStart(2, '0')}:{String(seconds).padStart(2, '0')}
      </p>
      <button onClick={handlePauseResumeClick}>
        {isRunning ? 'Pause' : 'Resume'}
      </button>
    </div>
  );
};

export default PomodoroTimer;
