import React, { useEffect } from 'react';

const AudioAlertOnRender = () => {
  const playAudio = () => {
    const audio = new Audio('./media/notification.mp3');
    audio.play();
  };

  useEffect(() => {
    return () => {
      // Cleanup function to stop the audio when the component is unmounted
      const audio = new Audio('./media/notification.mp3');
      audio.pause();
      audio.currentTime = 0;
    };
  }, []);

  return (
    <button onClick={playAudio}>
      Play Audio
    </button>
  );
};

export default AudioAlertOnRender;
